// src/pages/PatientView.jsx
import React, { useEffect, useState } from 'react';
import DashboardLayout from '../layouts/DashboardLayout';
import { useAuth } from '../context/AuthContext';
import { getData } from '../utils/localStorageHelpers';

function PatientView() {
  const { user } = useAuth();
  const [incidents, setIncidents] = useState([]);

  useEffect(() => {
    const all = getData('incidents') || [];
    const mine = all.filter(i => i.patientId === user.id);
    setIncidents(mine);
  }, [user]);

  return (
    <DashboardLayout>
      <h2 className="text-2xl font-bold mb-4">ðŸ‘¤ My Appointments & History</h2>

      <div className="bg-white p-4 rounded shadow">
        {incidents.length === 0 ? (
          <p>No records found.</p>
        ) : (
          <table className="w-full text-sm">
            <thead className="bg-gray-100">
              <tr>
                <th className="px-2 py-1">Title</th>
                <th className="px-2 py-1">Date</th>
                <th className="px-2 py-1">Status</th>
                <th className="px-2 py-1">Cost</th>
                <th className="px-2 py-1">Treatment</th>
                <th className="px-2 py-1">File</th>
              </tr>
            </thead>
            <tbody>
              {incidents.map((i) => (
                <tr key={i.id} className="border-t">
                  <td className="px-2 py-1">{i.title}</td>
                  <td className="px-2 py-1">{new Date(i.appointmentDate).toLocaleString()}</td>
                  <td className="px-2 py-1">{i.status}</td>
                  <td className="px-2 py-1">â‚¹{i.cost || '-'}</td>
                  <td className="px-2 py-1">{i.treatment || '-'}</td>
                  <td className="px-2 py-1">
                    {i.file ? (
                      <a href={i.file} download target="_blank" rel="noreferrer" className="text-blue-600 hover:underline">Download</a>
                    ) : '-'}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </DashboardLayout>
  );
}

export default PatientView;
