import React, { useEffect, useState } from "react";
import { getData } from "../utils/localStorageHelpers";
import { useAuth } from "../context/AuthContext";

function Dashboard() {
  const { user, logout } = useAuth();
  const [appointments, setAppointments] = useState([]);
  const [revenue, setRevenue] = useState(0);
  const [pending, setPending] = useState([]);
  const [topPatients, setTopPatients] = useState([]);

  useEffect(() => {
    const allIncidents = getData("incidents");
    const allPatients = getData("patients");

    const sorted = [...allIncidents].sort((a, b) =>
      new Date(a.appointmentDate) - new Date(b.appointmentDate)
    );
    const next10 = sorted.slice(0, 10);

    const revenueTotal = allIncidents.reduce((sum, i) =>
      i.status === "Completed" ? sum + Number(i.cost || 0) : sum, 0
    );

    const pendingList = allIncidents.filter(i => i.status !== "Completed");

    const patientCounts = {};
    allIncidents.forEach(i => {
      patientCounts[i.patientId] = (patientCounts[i.patientId] || 0) + 1;
    });
    const top = Object.entries(patientCounts)
      .sort((a, b) => b[1] - a[1])
      .slice(0, 5)
      .map(([pid, count]) => {
        const p = allPatients.find(p => p.id === pid);
        return { name: p?.name || "Unknown", count };
      });

    setAppointments(next10);
    setRevenue(revenueTotal);
    setPending(pendingList);
    setTopPatients(top);
  }, []);

  return (
    <div className="p-6 space-y-6 bg-gray-50 min-h-screen">
      <div className="flex justify-between items-center">
        <h1 className="text-3xl font-bold">Dashboard</h1>
        <button onClick={logout} className="bg-red-500 text-white px-4 py-2 rounded">
          Logout
        </button>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
        <div className="bg-white shadow rounded p-4">
          <h2 className="text-xl font-semibold">Next 10 Appointments</h2>
          <ul className="mt-2 text-sm">
            {appointments.map((a) => (
              <li key={a.id}>
                <strong>{a.title}</strong> – {new Date(a.appointmentDate).toLocaleString()}
              </li>
            ))}
          </ul>
        </div>

        <div className="bg-white shadow rounded p-4">
          <h2 className="text-xl font-semibold">Top Patients</h2>
          <ul className="mt-2 text-sm">
            {topPatients.map((p, i) => (
              <li key={i}>{p.name} – {p.count} visits</li>
            ))}
          </ul>
        </div>

        <div className="bg-white shadow rounded p-4">
          <h2 className="text-xl font-semibold">Pending Treatments</h2>
          <p className="text-3xl text-yellow-600">{pending.length}</p>
        </div>

        <div className="bg-white shadow rounded p-4">
          <h2 className="text-xl font-semibold">Total Revenue</h2>
          <p className="text-3xl text-green-600">₹ {revenue}</p>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
