import React, { useEffect, useState } from "react";
import { useAuth } from "../context/AuthContext";
import { getData } from "../utils/localStorageHelpers";

function Dashboard() {
  const { user, logout } = useAuth();
  const [appointments, setAppointments] = useState([]);
  const [topPatients, setTopPatients] = useState([]);
  const [pending, setPending] = useState([]);
  const [revenue, setRevenue] = useState(0);

  useEffect(() => {
    const incidents = getData("incidents") || [];
    const patients = getData("patients") || [];

    // Get next 5 appointments
    const upcoming = [...incidents]
      .sort((a, b) => new Date(a.appointmentDate) - new Date(b.appointmentDate))
      .slice(0, 5);

    // Top patients by number of incidents
    const patientCount = {};
    incidents.forEach((i) => {
      patientCount[i.patientId] = (patientCount[i.patientId] || 0) + 1;
    });
    const top = Object.entries(patientCount)
      .sort((a, b) => b[1] - a[1])
      .slice(0, 3)
      .map(([id, count]) => {
        const patient = patients.find((p) => p.id === id);
        return { name: patient?.name || "Unknown", count };
      });

    const pendingList = incidents.filter((i) => i.status !== "Completed");
    const totalRevenue = incidents
      .filter((i) => i.status === "Completed")
      .reduce((sum, i) => sum + Number(i.cost || 0), 0);

    setAppointments(upcoming);
    setTopPatients(top);
    setPending(pendingList);
    setRevenue(totalRevenue);
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold">Welcome, {user?.role}</h1>
        <button
          onClick={logout}
          className="px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600"
        >
          Logout
        </button>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div className="bg-white p-4 rounded shadow">
          <h2 className="text-lg font-semibold mb-2">🗓️ Next Appointments</h2>
          <ul className="text-sm text-gray-700">
            {appointments.length ? (
              appointments.map((a) => (
                <li key={a.id} className="border-b py-1">
                  {a.title} - {new Date(a.appointmentDate).toLocaleString()}
                </li>
              ))
            ) : (
              <li>No upcoming appointments</li>
            )}
          </ul>
        </div>

        <div className="bg-white p-4 rounded shadow">
          <h2 className="text-lg font-semibold mb-2">👥 Top Patients</h2>
          <ul className="text-sm text-gray-700">
            {topPatients.length ? (
              topPatients.map((p, i) => (
                <li key={i}>
                  {p.name} - {p.count} visits
                </li>
              ))
            ) : (
              <li>No patient activity</li>
            )}
          </ul>
        </div>

        <div className="bg-white p-4 rounded shadow">
          <h2 className="text-lg font-semibold mb-2">🧾 Pending Treatments</h2>
          <p className="text-3xl text-yellow-600">{pending.length}</p>
        </div>

        <div className="bg-white p-4 rounded shadow">
          <h2 className="text-lg font-semibold mb-2">💰 Total Revenue</h2>
          <p className="text-3xl text-green-600">₹ {revenue}</p>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
