import React, { useState, useEffect } from "react";
import { getData, setData, addItem, deleteItem } from "../utils/localStorageHelpers";
import { v4 as uuidv4 } from "uuid";

function Patients() {
  const [patients, setPatients] = useState([]);
  const [form, setForm] = useState({ name: "", dob: "", contact: "", healthInfo: "" });

  useEffect(() => {
    const data = getData("patients");
    setPatients(data);
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    const newPatient = { ...form, id: uuidv4() };
    addItem("patients", newPatient);
    setPatients([...patients, newPatient]);
    setForm({ name: "", dob: "", contact: "", healthInfo: "" });
  };

  const handleDelete = (id) => {
    deleteItem("patients", id);
    setPatients(patients.filter(p => p.id !== id));
  };

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">Patients</h2>
      <form onSubmit={handleSubmit} className="space-y-2">
        <input className="border p-2 w-full" placeholder="Name" value={form.name} onChange={e => setForm({ ...form, name: e.target.value })} required />
        <input className="border p-2 w-full" type="date" value={form.dob} onChange={e => setForm({ ...form, dob: e.target.value })} required />
        <input className="border p-2 w-full" placeholder="Contact" value={form.contact} onChange={e => setForm({ ...form, contact: e.target.value })} required />
        <input className="border p-2 w-full" placeholder="Health Info" value={form.healthInfo} onChange={e => setForm({ ...form, healthInfo: e.target.value })} />
        <button className="bg-blue-600 text-white px-4 py-2 rounded">Add Patient</button>
      </form>

      <div className="mt-6">
        <h3 className="text-lg font-semibold mb-2">Existing Patients</h3>
        <ul>
          {patients.map(p => (
            <li key={p.id} className="border p-2 mb-2 flex justify-between">
              <div>
                <strong>{p.name}</strong> â€” {p.contact} ({p.dob})
              </div>
              <button className="text-red-500" onClick={() => handleDelete(p.id)}>Delete</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Patients;
