// src/pages/Dashboard.jsx
import React from "react";
import { useAuth } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";

function Dashboard() {
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate("/login");
  };

  return (
    <div className="p-6">
      <h1 className="text-2xl font-bold mb-4">Welcome, {user.email}</h1>
      <p>Role: {user.role}</p>

      <div className="mt-4">
        {user.role === "Admin" ? (
          <div className="space-x-4">
            <button className="bg-green-500 text-white px-4 py-2 rounded" onClick={() => navigate("/patients")}>
              Manage Patients
            </button>
            <button className="bg-indigo-500 text-white px-4 py-2 rounded" onClick={() => navigate("/incidents")}>
              Manage Incidents
            </button>
          </div>
        ) : (
          <button className="bg-blue-500 text-white px-4 py-2 rounded" onClick={() => navigate("/me")}>
            My Appointments
          </button>
        )}
      </div>

      <button className="mt-8 bg-red-500 text-white px-4 py-2 rounded" onClick={handleLogout}>
        Logout
      </button>
    </div>
  );
}

export default Dashboard;
