import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useAuth } from "../context/AuthContext";

function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const { login } = useAuth();
  const navigate = useNavigate();
  const [error, setError] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    const success = login(email, password);
    if (success) {
      navigate("/");
    } else {
      setError("❌ Invalid credentials. Please try again.");
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-blue-100 to-blue-200">
      <div className="bg-white rounded-xl shadow-lg p-8 w-full max-w-md border border-blue-200">
        <div className="text-center mb-6">
          <h1 className="text-3xl font-extrabold text-blue-700">ENTNT Dental Center</h1>
          <p className="text-gray-600 mt-1">Login to your dashboard</p>
        </div>

        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Email</label>
            <input
              type="email"
              value={email}
              required
              onChange={(e) => setEmail(e.target.value)}
              className="w-full px-4 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:outline-none"
              placeholder="admin@entnt.in"
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-1">Password</label>
            <input
              type="password"
              value={password}
              required
              onChange={(e) => setPassword(e.target.value)}
              className="w-full px-4 py-2 border border-gray-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:outline-none"
              placeholder="Your password"
            />
          </div>

          {error && <p className="text-red-600 text-sm mt-2">{error}</p>}

          <button
            type="submit"
            className="w-full bg-blue-600 hover:bg-blue-700 transition text-white font-semibold py-2 rounded-md shadow-sm"
          >
            Sign In
          </button>
        </form>

        <div className="text-sm text-center text-gray-600 mt-6">
          <p className="mb-1">
            <span className="font-semibold text-blue-700">Demo Admin:</span>
            <span className="ml-1 bg-blue-100 px-2 py-0.5 rounded text-blue-800">admin@entnt.in</span> /
            <span className="ml-1 bg-blue-100 px-2 py-0.5 rounded text-blue-800">admin123</span>
          </p>
          <p>
            <span className="font-semibold text-green-700">Demo Patient:</span>
            <span className="ml-1 bg-green-100 px-2 py-0.5 rounded text-green-800">john@entnt.in</span> /
            <span className="ml-1 bg-green-100 px-2 py-0.5 rounded text-green-800">patient123</span>
          </p>
        </div>
      </div>
    </div>
  );
}

export default Login;
