import React, { useEffect, useState } from "react";
import { useAuth } from "../context/AuthContext";
import { getData } from "../utils/localStorageHelpers";

function PatientView() {
  const { user } = useAuth();
  const [incidents, setIncidents] = useState([]);
  const [patient, setPatient] = useState(null);

  useEffect(() => {
    const allPatients = getData("patients");
    const me = allPatients.find(p => p.id === user.patientId);
    setPatient(me);

    const allIncidents = getData("incidents");
    setIncidents(allIncidents.filter(i => i.patientId === user.patientId));
  }, [user.patientId]);

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">My Records</h2>
      {patient && (
        <div className="mb-4 p-4 bg-gray-100 rounded">
          <p><strong>Name:</strong> {patient.name}</p>
          <p><strong>Contact:</strong> {patient.contact}</p>
          <p><strong>Health Info:</strong> {patient.healthInfo}</p>
        </div>
      )}

      <h3 className="text-lg font-semibold mb-2">My Appointments</h3>
      <ul>
        {incidents.map(i => (
          <li key={i.id} className="border p-2 mb-2">
            <strong>{i.title}</strong> — {i.appointmentDate}<br />
            Status: {i.status}, Cost: ₹{i.cost}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default PatientView;
