// src/pages/PatientView.jsx
import React, { useEffect, useState } from 'react';
import DashboardLayout from '../layouts/DashboardLayout';
import { getData, setData } from '../utils/localStorageHelpers';
import { useAuth } from '../context/AuthContext';
import { v4 as uuid } from 'uuid';

function PatientView() {
  const { user } = useAuth();
  const [incidents, setIncidents] = useState([]);
  const [showForm, setShowForm] = useState(false);

  const [form, setForm] = useState({
    title: '',
    appointmentDate: '',
    description: '',
  });

  useEffect(() => {
  if (!user?.patientId) return;

  const all = getData('incidents') || [];
  const mine = all.filter(i => i.patientId === user.patientId);
  setIncidents(mine);
}, [user]);


  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const all = getData('incidents') || [];
    const newIncident = {
      id: uuid(),
      patientId: user.patientId,
      title: form.title || "Appointment Request",
      appointmentDate: form.appointmentDate,
      description: form.description,
      comments: "",
      cost: "",
      treatment: "",
      status: "Pending",
      nextDate: "",
      file: ""
    };

    const updated = [...all, newIncident];
    setData('incidents', updated);

    // Update local list and reset
    const mine = updated.filter(i => i.patientId === user?.patientId);
    setIncidents(mine);
    setForm({ title: '', appointmentDate: '', description: '' });
    setShowForm(false);
  };

  return (
    <DashboardLayout>
      <div className="mb-4 flex justify-between items-center">
        <h2 className="text-2xl font-semibold">🧾 My Appointments & History</h2>
        <button
          onClick={() => setShowForm(!showForm)}
          className="bg-blue-600 text-white px-4 py-2 rounded shadow hover:bg-blue-700 transition"
        >
          + Request Appointment
        </button>
      </div>

    {showForm && (
  <form onSubmit={handleSubmit} className="bg-white p-6 rounded shadow mb-6 space-y-4 border border-gray-200">
    <h3 className="text-lg font-semibold text-gray-700 mb-2">📅 Request an Appointment</h3>

    <div>
      <label className="block text-sm font-medium text-gray-600 mb-1">Title (optional)</label>
      <input
        type="text"
        name="title"
        placeholder="Eg: Toothache, Follow-up, etc."
        value={form.title}
        onChange={handleChange}
        className="input w-full"
      />
    </div>

    <div>
      <label className="block text-sm font-medium text-gray-600 mb-1">Requested Appointment Date & Time</label>
      <input
        type="datetime-local"
        name="appointmentDate"
        value={form.appointmentDate}
        onChange={handleChange}
        className="input w-full"
        required
      />
    </div>

    <div>
      <label className="block text-sm font-medium text-gray-600 mb-1">Symptoms / Notes</label>
      <textarea
        name="description"
        placeholder="Describe your symptoms or reason for this appointment"
        value={form.description}
        onChange={handleChange}
        className="input w-full"
        required
        rows={4}
      />
    </div>

    <div className="text-right">
      <button type="submit" className="bg-green-600 text-white px-5 py-2 rounded hover:bg-green-700">
        ✅ Submit Request
      </button>
    </div>
  </form>
)}


      <div className="bg-white p-4 rounded shadow">
        {incidents.length === 0 ? (
          <p>No records found.</p>
        ) : (
          <table className="w-full text-sm">
            <thead className="bg-gray-100">
              <tr>
                <th className="px-2 py-1 text-left">Date</th>
                <th className="px-2 py-1 text-left">Title</th>
                <th className="px-2 py-1 text-left">Status</th>
                <th className="px-2 py-1 text-left">Treatment</th>
                <th className="px-2 py-1 text-left">Cost</th>
              </tr>
            </thead>
            <tbody>
              {incidents.map((i) => (
                <tr key={i.id} className="border-t">
                  <td className="px-2 py-1">{new Date(i.appointmentDate).toLocaleString()}</td>
                  <td className="px-2 py-1">{i.title}</td>
                  <td className="px-2 py-1">{i.status}</td>
                  <td className="px-2 py-1">{i.treatment || '-'}</td>
                  <td className="px-2 py-1">{i.cost ? `₹${i.cost}` : '-'}</td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>
    </DashboardLayout>
  );
}

export default PatientView;
