import React, { useState, useEffect } from "react";
import { getData, addItem } from "../utils/localStorageHelpers";
import { v4 as uuidv4 } from "uuid";

function Incidents() {
  const [patients, setPatients] = useState([]);
  const [incidents, setIncidents] = useState([]);
  const [form, setForm] = useState({
    patientId: "",
    title: "",
    description: "",
    appointmentDate: "",
    comments: "",
  });

  useEffect(() => {
    setPatients(getData("patients"));
    setIncidents(getData("incidents"));
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    const newIncident = {
      ...form,
      id: uuidv4(),
      cost: 0,
      status: "Pending",
      files: [],
    };
    addItem("incidents", newIncident);
    setIncidents([...incidents, newIncident]);
    setForm({ patientId: "", title: "", description: "", appointmentDate: "", comments: "" });
  };

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">Incidents</h2>
      <form onSubmit={handleSubmit} className="space-y-2">
        <select className="border p-2 w-full" value={form.patientId} onChange={e => setForm({ ...form, patientId: e.target.value })} required>
          <option value="">Select Patient</option>
          {patients.map(p => (
            <option key={p.id} value={p.id}>{p.name}</option>
          ))}
        </select>
        <input className="border p-2 w-full" placeholder="Title" value={form.title} onChange={e => setForm({ ...form, title: e.target.value })} required />
        <textarea className="border p-2 w-full" placeholder="Description" value={form.description} onChange={e => setForm({ ...form, description: e.target.value })} />
        <input className="border p-2 w-full" type="datetime-local" value={form.appointmentDate} onChange={e => setForm({ ...form, appointmentDate: e.target.value })} />
        <input className="border p-2 w-full" placeholder="Comments" value={form.comments} onChange={e => setForm({ ...form, comments: e.target.value })} />
        <button className="bg-indigo-600 text-white px-4 py-2 rounded">Add Incident</button>
      </form>

      <div className="mt-6">
        <h3 className="text-lg font-semibold mb-2">Scheduled Incidents</h3>
        <ul>
          {incidents.map(i => (
            <li key={i.id} className="border p-2 mb-2">
              <strong>{i.title}</strong> for {patients.find(p => p.id === i.patientId)?.name || "Unknown"} on {i.appointmentDate}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Incidents;
