import React, { useEffect, useState } from 'react';
import DashboardLayout from '../layouts/DashboardLayout';
import { getData } from '../utils/localStorageHelpers';

function Dashboard() {
  const [appointments, setAppointments] = useState([]);
  const [patientsToday, setPatientsToday] = useState(0);
  const [totalPatients, setTotalPatients] = useState(0);
  const [requests, setRequests] = useState(0);

  useEffect(() => {
    const allPatients = getData('patients') || [];
    const allIncidents = getData('incidents') || [];

    const today = new Date().toISOString().slice(0, 10);
    const todaysAppointments = allIncidents.filter(i => i.appointmentDate?.startsWith(today));
    const upcoming = allIncidents.filter(i => new Date(i.appointmentDate) >= new Date());

    setAppointments(upcoming.slice(0, 5));
    setPatientsToday(todaysAppointments.length);
    setTotalPatients(allPatients.length);
    setRequests(allIncidents.length);
  }, []);

  return (
    <DashboardLayout>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
        <StatCard label="Patients Today" value={patientsToday} />
        <StatCard label="Total Patients" value={totalPatients} />
        <StatCard label="Requests" value={requests} />
      </div>

      <div className="bg-white p-4 rounded shadow">
        <h2 className="text-lg font-semibold mb-4">Upcoming Appointments</h2>
        <ul className="divide-y">
          {appointments.length ? appointments.map((appt) => (
            <li key={appt.id} className="py-2 flex justify-between">
              <span>{appt.title}</span>
              <span className="text-gray-500 text-sm">{new Date(appt.appointmentDate).toLocaleString()}</span>
            </li>
          )) : <p>No upcoming appointments</p>}
        </ul>
      </div>
    </DashboardLayout>
  );
}

function StatCard({ label, value }) {
  return (
    <div className="bg-white rounded shadow p-4 text-center">
      <p className="text-sm text-gray-500 mb-2">{label}</p>
      <p className="text-3xl font-bold text-blue-600">{value}</p>
    </div>
  );
}

export default Dashboard;
